elements.expression:
filters
| essql 
  query="select (cast(e as float)*100/cast(a as float)) as ae , app, a , e from (select count(context.tags.application) as a, context.tags.application as app, count(context.tags.error_code) as e from \"apm-*\" where context.tags.application is not null group by context.tags.application) "
| pointseries x="ae" y="app" color="ae"
| plot defaultStyle={seriesStyle bars=0.75 horizontalBars=true} legend=false palette={palette "#7fff00" "ffff00" "#800000" gradient=true} 
  font={font family="'Open Sans', Helvetica, Arial, sans-serif" size=14 align="left" color="#f2bc33" weight="normal" underline=false italic=false} xaxis=true seriesStyle={seriesStyle} yaxis=true
| render css=".canvasRenderEl {

}" containerStyle={containerStyle border="00px ridge #FFFFFF" borderRadius="0px"}

filters
| essql query="SELECT count(distinct 
 context.tags.application)  as cnt FROM \"apm-*\""
| math "cnt"
| metric "Total Number Of Applications" 
  metricFont={font family="'Open Sans', Helvetica, Arial, sans-serif" size=48 align="center" color="#000000" weight="bold" underline=false italic=false} 
  labelFont={font family="'Open Sans', Helvetica, Arial, sans-serif" size=14 align="center" color="#000000" weight="normal" underline=false italic=false}
| render containerStyle={containerStyle border="03px outset "}

filters
| essql 
  query="SELECT err from(select (a*100/b) as err from(select count(context.tags.application) as b, count(context.tags.error_code) as a from \"apm-*\" where context.tags.application is not null group by context.tags.application)) where err > 60"
| math "size(err)"
| metric "Applications with poor health" 
  metricFont={font family="'Open Sans', Helvetica, Arial, sans-serif" size=48 align="center" color="#fd7643" weight="bold" underline=false italic=false} 
